var checkboxElems;
window.onload = function(){
    checkboxElems = document.querySelectorAll("input[type='checkbox']");
    for (var i = 0; i < checkboxElems.length; i++) {
    checkboxElems[i].addEventListener("click", filterArticles)
    }
};

function filterArticles(){
    const allcard = document.getElementsByClassName('.card')
    for (var i = 0; i < allcard.length; i++) {
        allcard[i].addEventListener("click", filterArticles)
        }

}
