    const arrayLanguage = []
    var test  = document.getElementById("languageTarget").children
    // document.getElementById("languageTarget").children.forEach(element => {
    //     arrayLanguage.append(element.id)
    // });
    for ( var x = 0; x < arrayLanguage.length ; x++){
        console.log(test[x].id)
    }
}

